Dir.each_child(".").select{(1..25).include? _1.to_i}.sort.map{Dir.chdir(_1){[File.exist?("solution-1.rb") ? File.read("solution-1.rb") : "puts 'No solution :('", File.exist?("solution-2.rb") ? File.read("solution-2.rb") : "puts 'No solution :('"]}}.each_with_index.map{|v,i| Dir.chdir((i+1).to_s){eval("print 'Solution 1 from day #{i+1}: '\n" + v[0] + "print 'Solution 2 from day #{i+1}: '\n" + v[1])}}
